mapper.select:
SELECT ID as "id", SPECIAL_KNOWLEDGE_ID as "specialKnowledgeId", CREATE_RULE as "createRule", OBTAIN_POINT as "obtainPoint", CREATE_WAY as "createWay", IS_SHOW_ANSWER as "isShowAnswer", QUESTION_DIFFICULTY as "questionDifficulty", BEGIN_TIME as "beginTime", CREATE_USER as "createUser", QUESTION_TYPE as "questionType", RULE_NAME as "ruleName", USERID2NAME(CREATE_USER) AS "creatUserName" FROM LAW_DAILY_QUESTION d
ORDER BY ${page.orderBy}
ORDER BY BEGIN_TIME DESC
