mapper.select:
SELECT ID as "id", SPECIAL_KNOWLEDGE_ID as "specialKnowledgeId", CREATE_RULE as "createRule", OBTAIN_POINT as "obtainPoint", IS_SHOW_LEGAL as "isShowLegal", CREATE_WAY as "createWay", IS_SHOW_ANSWER as "isShowAnswer", QUESTION_DIFFICULTY as "questionDifficulty", JOIN_PEOPLE as "joinPeople", JOIN_DEPT as "joinDept", QUESTION_ID as "questionId", BEGIN_TIME as "beginTime", END_TIME as "endTime", CREATE_USER as "createUser", QUESTION_TYPE as "questionType" FROM LAW_DAILY_QUESTION d where 1 = 1
AND d.IS_SHOW_LEGAL = #{isShowLegal}
AND d.SPECIAL_KNOWLEDGE_ID = #{specialKnowledgeId}
AND d.IS_SHOW_ANSWER = #{isShowAnswer}
AND d.CREATE_RULE = #{createRule}
AND d.QUESTION_DIFFICULTY = #{questionDifficulty}
AND a.question_type = #{questionType}

select ID from (select ID,QUESTION_DIFFICULTY,SPECIAL_KNOWLEDGE_ID,QUESTION_TYPE from LAW_TESTQUESTIONS order by dbms_random.random)l
where
(select to_char(sysdate,'yyyy-MM-dd ') from dual)
BETWEEN
to_char(l.BEGIN_TIME,'yyyy-MM-dd ') AND to_char(l.END_TIME,'yyyy-MM-dd')

    and l.SPECIAL_KNOWLEDGE_ID = #{specialKnowledgeId}


    and l.QUESTION_DIFFICULTY = #{questionDifficulty}


    and l.QUESTION_TYPE = #{questionType}

and rownum <= 1
