mapper.select:
SELECT ID as "id", SPECIAL_KNOWLEDGE_ID as "specialKnowledgeId", CREATE_RULE as "createRule", OBTAIN_POINT as "obtainPoint", IS_SHOW_LEGAL as "isShowLegal", CREATE_WAY as "createWay", IS_SHOW_ANSWER as "isShowAnswer", QUESTION_DIFFICULTY as "questionDifficulty", JOIN_PEOPLE as "joinPeople", JOIN_DEPT as "joinDept", QUESTION_ID as "questionId", BEGIN_TIME as "beginTime", END_TIME as "endTime", CREATE_USER as "createUser", QUESTION_TYPE as "questionType", RULE_NAME as "ruleName", USERID2NAME(CREATE_USER) AS "creatUserName" FROM LAW_DAILY_QUESTION d where 1 = 1
AND d.IS_SHOW_LEGAL = #{isShowLegal}
AND d.SPECIAL_KNOWLEDGE_ID = #{specialKnowledgeId}
AND d.IS_SHOW_ANSWER = #{isShowAnswer}
AND d.CREATE_RULE = #{createRule}
AND d.QUESTION_DIFFICULTY = #{questionDifficulty}
AND a.question_type = #{questionType}

SELECT ID FROM ( SELECT s. ID, s.QUESTION_DIFFICULTY, s.SPECIAL_KNOWLEDGE_ID, s.QUESTION_TYPE, q.BEGIN_TIME, q.END_TIME FROM LAW_TESTQUESTIONS s RIGHT JOIN LAW_DAILY_QUESTION q ON s. ID = q.QUESTION_ID ORDER BY dbms_random. RANDOM ) l WHERE ( SELECT TO_CHAR (SYSDATE, 'yyyy-MM-dd') FROM dual ) BETWEEN TO_CHAR (l.BEGIN_TIME, 'yyyy-MM-dd') AND TO_CHAR (l.END_TIME, 'yyyy-MM-dd')
and l.SPECIAL_KNOWLEDGE_ID = #{specialKnowledgeId}
and l.QUESTION_DIFFICULTY = #{questionDifficulty}
and rownum <= 1
