mapper.select:
SELECT collectionId, ID, stu_title, stu_type, VIDEO_PIC_ACC, collecttime, STU_ISSTIME, STU_ISSUER, COM_CONTENT, STU_LAWID FROM ( SELECT LAW_PERSONAL_COLLECTION. ID collectionId, LAW_STU_MEDIA. ID, LAW_STU_MEDIA.stu_title, LAW_STU_MEDIA.stu_type, LAW_STU_MEDIA.VIDEO_PIC_ACC, LAW_STU_MEDIA.STU_ISSUER, LAW_STU_MEDIA.STU_ISSTIME, LAW_STU_MEDIA.STU_LAWID, LAW_STU_MEDIA.COM_CONTENT, LAW_PERSONAL_COLLECTION.CREATE_TIME collecttime, ROWNUM AS rowno FROM LAW_PERSONAL_COLLECTION, LAW_STU_MEDIA WHERE LAW_PERSONAL_COLLECTION.SOURCE_ID = LAW_STU_MEDIA. ID AND LAW_PERSONAL_COLLECTION.CREATE_USER = #{userId} AND LAW_PERSONAL_COLLECTION. TYPE = 1 AND LAW_STU_MEDIA. ID is not null AND ROWNUM <= #{endPage}
AND LAW_STU_MEDIA.stu_title LIKE '%' || #{stuTitle} || '%'
AND LAW_STU_MEDIA.STU_ISSUER LIKE '%' || #{stuIssuer} || '%'
AND LAW_STU_MEDIA.STU_LAWID = #{stuLawid}
AND LAW_PERSONAL_COLLECTION.CREATE_TIME >= TO_DATE(#{startTime}, 'yyyy-mm-dd')
AND LAW_PERSONAL_COLLECTION.CREATE_TIME < = TO_DATE(#{endTime}, 'yyyy-mm-dd')
AND LAW_STU_MEDIA.stu_type = #{stuType}
ORDER BY LAW_PERSONAL_COLLECTION.CREATE_TIME DESC ) WHERE rowno > #{startPage}

select DISTINCT id,info_id,to_char(info_name) as infoName,info_parent_id,task_id,CREATE_time,info_code,info_type,data_id,recordId from ( select aa.*, r.id as recordId FROM( SELECT REGEXP_SUBSTR ( T .INFO_PARENT_ID, '[^,]+', 1, ROWNUM ) AS PARENT_ID, T .* FROM LAW_TASKS_DESIC T CONNECT BY ROWNUM <= LENGTH (T .INFO_PARENT_ID) - LENGTH ( REGEXP_REPLACE (T .INFO_PARENT_ID, ',', '') ) + 1 ) aa LEFT JOIN LAW_STU_RECORD r ON aa.id=r.stu_id and r.task_id=#{taskId} and r.user_id=#{userId} where 1=1
AND aa.INFO_TYPE = 'stu_'||#{infoType}
AND aa.TASK_ID = #{taskId}
and aa.PARENT_ID in (select "ID" FROM LAW_CLASSIFY T START WITH T .id = #{infoId} CONNECT BY PRIOR T .id = T .PARENT_ID)
union all
select aa.*, r.id as recordId FROM( SELECT REGEXP_SUBSTR ( T .INFO_PARENT_ID, '[^,]+', 1, ROWNUM ) AS PARENT_ID, T .* FROM LAW_TASKS_DESIC T CONNECT BY ROWNUM <= LENGTH (T .INFO_PARENT_ID) - LENGTH ( REGEXP_REPLACE (T .INFO_PARENT_ID, ',', '') ) + 1 )aa LEFT JOIN LAW_STU_RECORD r ON aa.id=r.stu_id and r.task_id=#{taskId} and r.user_id=#{userId} where 1=1
AND aa.INFO_TYPE = 'case_'||#{infoType}
AND aa.TASK_ID = #{taskId}
and aa.PARENT_ID in (select "ID" FROM LAW_CLASSIFY T START WITH T .id = #{infoId} CONNECT BY PRIOR T .id = T .PARENT_ID)
)

SELECT count(distinct id) FROM ( select aa.*, r.id as recordId FROM( SELECT REGEXP_SUBSTR ( T .INFO_PARENT_ID, '[^,]+', 1, ROWNUM ) AS PARENT_ID, T .* FROM LAW_TASKS_DESIC T CONNECT BY ROWNUM <= LENGTH (T .INFO_PARENT_ID) - LENGTH ( REGEXP_REPLACE (T .INFO_PARENT_ID, ',', '') ) + 1 ) aa LEFT JOIN LAW_STU_RECORD r ON aa.id=r.stu_id and r.task_id=#{taskId} and r.user_id=#{userId} where 1=1
AND aa.INFO_TYPE = 'stu_'||#{infoType}
AND aa.TASK_ID = #{taskId}
and aa.PARENT_ID in (select "ID" FROM LAW_CLASSIFY T START WITH T .id = #{infoId} CONNECT BY PRIOR T .id = T .PARENT_ID)
union all
select aa.*, r.id as recordId FROM( SELECT REGEXP_SUBSTR ( T .INFO_PARENT_ID, '[^,]+', 1, ROWNUM ) AS PARENT_ID, T .* FROM LAW_TASKS_DESIC T CONNECT BY ROWNUM <= LENGTH (T .INFO_PARENT_ID) - LENGTH ( REGEXP_REPLACE (T .INFO_PARENT_ID, ',', '') ) + 1 )aa LEFT JOIN LAW_STU_RECORD r ON aa.id=r.stu_id and r.task_id=#{taskId} and r.user_id=#{userId} where 1=1
AND aa.INFO_TYPE = 'case_'||#{infoType}
AND aa.TASK_ID = #{taskId}
and aa.PARENT_ID in (select "ID" FROM LAW_CLASSIFY T START WITH T .id = #{infoId} CONNECT BY PRIOR T .id = T .PARENT_ID)
)

mapper.update:
UPDATE LAW_STU_MEDIA
SET DEL_STATUS = #{status}
WHERE id = #{id}

UPDATE LAW_STU_MEDIA
SET STRUTS = #{status}
WHERE id = #{id}
