mapper.select:
SELECT collectionId, ID, stu_title, stu_type, VIDEO_PIC_ACC, collecttime, STU_ISSTIME, STU_ISSUER, COM_CONTENT, STU_LAWID FROM ( SELECT LAW_COLLECTION. ID collectionId, LAW_STU_MEDIA. ID, LAW_STU_MEDIA.stu_title, LAW_STU_MEDIA.stu_type, LAW_STU_MEDIA.VIDEO_PIC_ACC, LAW_STU_MEDIA.STU_ISSUER, LAW_STU_MEDIA.STU_ISSTIME, LAW_STU_MEDIA.STU_LAWID, LAW_STU_MEDIA.COM_CONTENT, LAW_COLLECTION.OPTTIME collecttime, ROWNUM AS rowno FROM LAW_COLLECTION,LAW_STU_MEDIA WHERE LAW_COLLECTION.COM_STUCODE = LAW_STU_MEDIA. ID and LAW_COLLECTION.COM_USERID = #{userId} AND LAW_COLLECTION. TYPE = 10 and LAW_STU_MEDIA. ID is not null AND ROWNUM <= #{endPage} And LAW_COLLECTION.DEL_STATUS=0
and LAW_STU_MEDIA.stu_title like '%' || #{stuTitle} || '%'
and LAW_STU_MEDIA.STU_ISSUER like '%' || #{stuIssuer} || '%'
and LAW_STU_MEDIA.STU_LAWID = #{stuLawid}
and LAW_COLLECTION.OPTTIME >= TO_DATE(#{startTime}, 'yyyy-mm-dd')
and LAW_COLLECTION.OPTTIME < = TO_DATE(#{endTime}, 'yyyy-mm-dd')
and LAW_STU_MEDIA.stu_type = #{stuType}
order by LAW_COLLECTION.OPTTIME desc ) WHERE rowno > #{startPage}

select DISTINCT id,info_id,info_name,info_parent_id,task_id,CREATE_time,info_code,info_type,data_id,recordId from ( select aa.*, r.id as recordId FROM( SELECT REGEXP_SUBSTR ( T .INFO_PARENT_ID, '[^,]+', 1, ROWNUM ) AS PARENT_ID, T .* FROM LAW_TASKS_DESIC T CONNECT BY ROWNUM <= LENGTH (T .INFO_PARENT_ID) - LENGTH ( REGEXP_REPLACE (T .INFO_PARENT_ID, ',', '') ) + 1 ) aa LEFT JOIN LAW_STU_RECORD r ON aa.id=r.stu_id and r.task_id=#{taskId} and r.user_id=#{userId} where 1=1
AND aa.INFO_TYPE = 'stu_'||#{infoType}
AND aa.TASK_ID = #{taskId}
and aa.PARENT_ID in (select "ID" FROM LAW_CLASSIFY T START WITH T .id = #{infoId} CONNECT BY PRIOR T .id = T .PARENT_ID)
union all
select aa.*, r.id as recordId FROM( SELECT REGEXP_SUBSTR ( T .INFO_PARENT_ID, '[^,]+', 1, ROWNUM ) AS PARENT_ID, T .* FROM LAW_TASKS_DESIC T CONNECT BY ROWNUM <= LENGTH (T .INFO_PARENT_ID) - LENGTH ( REGEXP_REPLACE (T .INFO_PARENT_ID, ',', '') ) + 1 )aa LEFT JOIN LAW_STU_RECORD r ON aa.id=r.stu_id and r.task_id=#{taskId} and r.user_id=#{userId} where 1=1
AND aa.INFO_TYPE = 'case_'||#{infoType}
AND aa.TASK_ID = #{taskId}
and aa.PARENT_ID in (select "ID" FROM LAW_CLASSIFY T START WITH T .id = #{infoId} CONNECT BY PRIOR T .id = T .PARENT_ID)
)

SELECT count(distinct id) FROM ( select aa.*, r.id as recordId FROM( SELECT REGEXP_SUBSTR ( T .INFO_PARENT_ID, '[^,]+', 1, ROWNUM ) AS PARENT_ID, T .* FROM LAW_TASKS_DESIC T CONNECT BY ROWNUM <= LENGTH (T .INFO_PARENT_ID) - LENGTH ( REGEXP_REPLACE (T .INFO_PARENT_ID, ',', '') ) + 1 ) aa LEFT JOIN LAW_STU_RECORD r ON aa.id=r.stu_id and r.task_id=#{taskId} and r.user_id=#{userId} where 1=1
AND aa.INFO_TYPE = 'stu_'||#{infoType}
AND aa.TASK_ID = #{taskId}
and aa.PARENT_ID in (select "ID" FROM LAW_CLASSIFY T START WITH T .id = #{infoId} CONNECT BY PRIOR T .id = T .PARENT_ID)
union all
select aa.*, r.id as recordId FROM( SELECT REGEXP_SUBSTR ( T .INFO_PARENT_ID, '[^,]+', 1, ROWNUM ) AS PARENT_ID, T .* FROM LAW_TASKS_DESIC T CONNECT BY ROWNUM <= LENGTH (T .INFO_PARENT_ID) - LENGTH ( REGEXP_REPLACE (T .INFO_PARENT_ID, ',', '') ) + 1 )aa LEFT JOIN LAW_STU_RECORD r ON aa.id=r.stu_id and r.task_id=#{taskId} and r.user_id=#{userId} where 1=1
AND aa.INFO_TYPE = 'case_'||#{infoType}
AND aa.TASK_ID = #{taskId}
and aa.PARENT_ID in (select "ID" FROM LAW_CLASSIFY T START WITH T .id = #{infoId} CONNECT BY PRIOR T .id = T .PARENT_ID)
)

mapper.update:
UPDATE LAW_STU_MEDIA
SET DEL_STATUS = #{status}
WHERE id = #{id}

mapper.select:
SELECT count(1) FROM ( SELECT A . ID AS dataId, A . ID AS infoId, A .STU_CODE AS infoCode, A .STU_TITLE AS infoName, A .STU_LAWID AS infoParentId, 'stu_' || A .stu_type || '_data' AS infoType FROM LAW_STU_MEDIA A
where A.DEL_STATUS = 0
AND FIND_IN_SET(#{dataId},A .STU_LAWID) > 0
AND A .stu_type = #{infoType}
union ALL
SELECT A .ID AS dataId, 'case_'||A.case_type||'_'||A.ID AS infoId, A .CASE_CODE AS infoCode, A .CASE_TITLE AS infoName, A.case_Lawid, 'case_'||A.content_type||'_data' AS infoType FROM LAW_CASE_ANALYSIS A where A.DEL_STATUS = 0
AND FIND_IN_SET(#{dataId},A .case_Lawid) > 0
AND A .content_type = #{infoType}
)
