mapper.select:
SELECT
count(LAW_TESTQUESTIONS. ID)
FROM
LAW_COLLECTION
LEFT JOIN LAW_TESTQUESTIONS ON LAW_COLLECTION.COM_STUCODE = LAW_TESTQUESTIONS. ID
WHERE
LAW_COLLECTION.COM_USERID = #{userId}
AND LAW_COLLECTION. TYPE = 20
And LAW_COLLECTION.DEL_STATUS=0

    AND QUESTION_TYPE=#{questionType}


    AND TYPE_ID=#{typeId}


    AND QUESTION_DIFFICULTY=#{questionDifficulty}

select collectionId, ID, COM_CONTENT, QUESTION_TYPE, questiontypeValue, ANSWER_ID, answerValue, QUESTION_DIFFICULTY, questiondifficultyValue, TYPE_ID, typeValue, SPECIAL_KNOWLEDGE_ID, specialknowledgeValue from ( SELECT LAW_COLLECTION. ID as collectionId, LAW_TESTQUESTIONS. ID, LAW_TESTQUESTIONS.COM_CONTENT, LAW_TESTQUESTIONS.QUESTION_TYPE, (SELECT VALUE FROM LAW_SYS_DICT WHERE ID = QUESTION_TYPE )AS questiontypeValue, LAW_TESTQUESTIONS.ANSWER_ID, (SELECT QUESTION_CONTENT FROM LAW_ANSWER WHERE ID = ANSWER_ID )AS answerValue, LAW_TESTQUESTIONS.QUESTION_DIFFICULTY, (SELECT VALUE FROM LAW_SYS_DICT WHERE ID = QUESTION_DIFFICULTY )AS questiondifficultyValue, LAW_TESTQUESTIONS.TYPE_ID, (SELECT VALUE FROM LAW_SYS_DICT WHERE ID = TYPE_ID )AS typeValue, LAW_TESTQUESTIONS.SPECIAL_KNOWLEDGE_ID, (SELECT VALUE FROM LAW_SYS_DICT WHERE ID = SPECIAL_KNOWLEDGE_ID )AS specialknowledgeValue, ROWNUM AS rowno FROM LAW_COLLECTION LEFT JOIN LAW_TESTQUESTIONS ON LAW_COLLECTION.COM_STUCODE = LAW_TESTQUESTIONS. ID WHERE LAW_COLLECTION.COM_USERID = #{userId} And LAW_COLLECTION.DEL_STATUS=0 And ROWNUM <= #{endPage} AND LAW_COLLECTION. TYPE = 20
AND QUESTION_TYPE=#{questionType}
AND TYPE_ID=#{typeId}
AND QUESTION_DIFFICULTY=#{questionDifficulty}
) WHERE rowno > #{startPage}

SELECT ID FROM ( SELECT LAW_TESTQUESTIONS. ID FROM LAW_COLLECTION LEFT JOIN LAW_TESTQUESTIONS ON LAW_COLLECTION.COM_STUCODE = LAW_TESTQUESTIONS. ID WHERE LAW_COLLECTION.COM_USERID = #{userId} AND LAW_COLLECTION. TYPE = 10 AND LAW_COLLECTION.DEL_STATUS = 0
AND QUESTION_TYPE=#{questionType}
AND TYPE_ID=#{typeId}
AND QUESTION_DIFFICULTY=#{questionDifficulty}
ORDER BY dbms_random.VALUE ) WHERE ROWNUM <= #{num}

SELECT ID, recordId, COM_CONTENT, QUESTION_TYPE, questiontypeValue, ANSWER_ID, answerValue, QUESTION_DIFFICULTY, questiondifficultyValue, TYPE_ID, typeValue, SPECIAL_KNOWLEDGE_ID, specialknowledgeValue from ( SELECT T . ID, r. ID recordId, T .COM_CONTENT, T .QUESTION_TYPE, (SELECT VALUE FROM LAW_SYS_DICT WHERE ID = QUESTION_TYPE )AS questiontypeValue, T .ANSWER_ID, (SELECT QUESTION_CONTENT FROM LAW_ANSWER WHERE ID = ANSWER_ID )AS answerValue, T .QUESTION_DIFFICULTY, (SELECT VALUE FROM LAW_SYS_DICT WHERE ID = QUESTION_DIFFICULTY )AS questiondifficultyValue, T .TYPE_ID, (SELECT VALUE FROM LAW_SYS_DICT WHERE ID = TYPE_ID )AS typeValue, T .SPECIAL_KNOWLEDGE_ID, (SELECT VALUE FROM LAW_SYS_DICT WHERE ID = SPECIAL_KNOWLEDGE_ID )AS specialknowledgeValue, ROWNUM AS rowno FROM LAW_USER_QUESTION_RECORD r INNER JOIN LAW_TESTQUESTIONS T ON r.QUEST_ID = T . ID WHERE r.USER_ID = #{userId} And ROWNUM <= #{endPage} AND r.MY_ASWER_ID != T .ANSWER_ID
AND T .QUESTION_DIFFICULTY =#{questionType}
AND T .SPECIAL_KNOWLEDGE_ID = #{typeId}
AND QUESTION_TYPE = #{questionDifficulty}
) WHERE rowno > #{startPage}

SELECT
count(T . ID)
FROM
LAW_USER_QUESTION_RECORD r
INNER JOIN LAW_TESTQUESTIONS T ON r.QUEST_ID = T . ID
WHERE
r.USER_ID = #{userId}
AND r.MY_ASWER_ID != T .ANSWER_ID

    AND T .QUESTION_DIFFICULTY =#{questionType}


    AND T .SPECIAL_KNOWLEDGE_ID = #{typeId}


    AND QUESTION_TYPE = #{questionDifficulty}

SELECT
ID
FROM
(
  SELECT
  T . ID
  FROM
  LAW_USER_QUESTION_RECORD r
  INNER JOIN LAW_TESTQUESTIONS T ON r.QUEST_ID = T . ID
  WHERE
  r.USER_ID = #{userId}
  AND r.MY_ASWER_ID != T .ANSWER_ID

  AND QUESTION_TYPE=#{questionType}


  AND TYPE_ID=#{typeId}


  AND QUESTION_DIFFICULTY=#{questionDifficulty}

ORDER BY
dbms_random.VALUE
)
WHERE
ROWNUM <= #{num}
