mapper.select:
SELECT
count(LAW_TESTQUESTIONS. ID)
FROM
LAW_COLLECTION
LEFT JOIN LAW_TESTQUESTIONS ON LAW_COLLECTION.COM_STUCODE = LAW_TESTQUESTIONS. ID
WHERE
LAW_COLLECTION.COM_USERID = #{userId}
AND LAW_COLLECTION. TYPE = 20
and LAW_TESTQUESTIONS. ID is not null
And LAW_COLLECTION.DEL_STATUS=0

    AND QUESTION_TYPE=#{questionType}


    AND SPECIAL_KNOWLEDGE_ID=#{specialKnowledgeId}


    AND QUESTION_DIFFICULTY=#{questionDifficulty}

select collectionId, ID, COM_CONTENT, QUESTION_TYPE, questiontypeValue, ANSWER_ID, QUESTION_DIFFICULTY, questiondifficultyValue, TYPE_ID, typeValue, SPECIAL_KNOWLEDGE_ID, specialknowledgeValue, ANSWER_DESCRIBLE from ( SELECT LAW_COLLECTION. ID as collectionId, LAW_TESTQUESTIONS.ANSWER_DESCRIBLE, LAW_TESTQUESTIONS. ID, LAW_TESTQUESTIONS.COM_CONTENT, LAW_TESTQUESTIONS.QUESTION_TYPE, (SELECT VALUE FROM LAW_SYS_DICT WHERE CODE = QUESTION_TYPE )AS questiontypeValue, LAW_TESTQUESTIONS.ANSWER_ID, LAW_TESTQUESTIONS.QUESTION_DIFFICULTY, (SELECT VALUE FROM LAW_SYS_DICT WHERE CODE = QUESTION_DIFFICULTY )AS questiondifficultyValue, LAW_TESTQUESTIONS.TYPE_ID, (SELECT VALUE FROM LAW_SYS_DICT WHERE CODE = TYPE_ID )AS typeValue, LAW_TESTQUESTIONS.SPECIAL_KNOWLEDGE_ID, (SELECT VALUE FROM LAW_SYS_DICT WHERE CODE = SPECIAL_KNOWLEDGE_ID )AS specialknowledgeValue, ROWNUM AS rowno FROM LAW_COLLECTION LEFT JOIN LAW_TESTQUESTIONS ON LAW_COLLECTION.COM_STUCODE = LAW_TESTQUESTIONS. ID WHERE LAW_COLLECTION.COM_USERID = #{userId} and LAW_TESTQUESTIONS. ID is not null And LAW_COLLECTION.DEL_STATUS=0 And ROWNUM <= #{endPage} AND LAW_COLLECTION. TYPE = 20
AND QUESTION_TYPE=#{questionType}
AND SPECIAL_KNOWLEDGE_ID=#{specialKnowledgeId}
AND QUESTION_DIFFICULTY=#{questionDifficulty}
) WHERE rowno > #{startPage}

SELECT ID FROM ( SELECT LAW_TESTQUESTIONS. ID FROM LAW_COLLECTION LEFT JOIN LAW_TESTQUESTIONS ON LAW_COLLECTION.COM_STUCODE = LAW_TESTQUESTIONS. ID WHERE LAW_COLLECTION.COM_USERID = #{userId} AND LAW_COLLECTION. TYPE = 20 AND LAW_COLLECTION.DEL_STATUS = 0
AND QUESTION_TYPE=#{questionType}
AND SPECIAL_KNOWLEDGE_ID=#{specialKnowledgeId}
AND QUESTION_DIFFICULTY=#{questionDifficulty}
ORDER BY dbms_random.VALUE ) WHERE ROWNUM <= #{num}

mapper.update:
