ORDER BY dbms_random.random

select ID,COM_CONTENT, QUESTION_TYPE, (SELECT VALUE FROM LAW_SYS_DICT WHERE CODE = QUESTION_TYPE )AS questiontypeValue, ANSWER_ID,ANSWER_DESCRIBLE, QUESTION_DIFFICULTY, (SELECT VALUE FROM LAW_SYS_DICT WHERE CODE = QUESTION_DIFFICULTY )AS questiondifficultyValue, TYPE_ID, (SELECT VALUE FROM LAW_SYS_DICT WHERE CODE = TYPE_ID )AS typeValue, SPECIAL_KNOWLEDGE_ID, ( SELECT
name
FROM LAW_SYS_TOPIC WHERE id = SPECIAL_KNOWLEDGE_ID )AS specialknowledgeValue FROM law_testquestions where id=#{id}

SELECT

FROM law_testquestions a

    a.is_enble != '2'
    AND a.STATUS='1'

        AND a.type_id = #{typeId}


        AND a.question_difficulty = #{questionDifficulty}


        AND a.question_type = #{questionType}


        AND a.is_enble = #{isEnble}


        AND a.com_content like concat(concat('%',#{comContent}),'%')





        ORDER BY ${page.orderBy}


        ORDER BY a.create_time DESC

SELECT * FROM (SELECT
FROM law_testquestions a
a.is_enble = '0' AND a.STATUS='1' AND a.RELEASE_STATUS='1'
