mapper.delete:
DELETE FROM law_post_collection
WHERE post_id IN

    #{item}

mapper.insert:
INSERT INTO law_post_collection
	(id,
	post_id,
	user_id
	)
VALUES
	(#{id},
	#{postId},
	#{userId}
	)

mapper.delete:
DELETE FROM law_post_collection
WHERE a.user_id in

    #{item}

mapper.select:
SELECT a.id AS "id", u.user_name AS "userName", a.content AS "content", a.subordinate_column AS "subordinateColumn", a.collection_num AS "collectionNum", a.comment_num AS "commentNum", a.report_num AS "reportNum", a.create_time AS "createTime" FROM law_post_collection pc INNER JOIN law_post a ON pc.post_id = a.id INNER JOIN law_user u ON u.id = a.create_user
pc.user_id = #{userId}
AND a.subordinate_column = #{subordinateColumn}
