mapper.select:
SELECT aaa.ID as "id", aaa.BATTLE_PLATFORM_ID as "battlePlatformId", aaa.USER_ID as "userId", USERID2NAME(aaa.USER_ID) as "userName", aaa.WHETHER_WIN as "whetherWin", aaa.SCORE as "score", aaa.TYPE as "type", aaa.CREATE_TIME as "createTime", CASE WHEN aaa.user_id= BBB.play1 THEN BBB.play2 WHEN aaa.user_id= BBB.play2 THEN BBB.play1 END AS enemyid , CASE WHEN aaa.user_id= BBB.play1 THEN USERID2NAME(BBB.play2) WHEN aaa.user_id= BBB.play2 THEN USERID2NAME(BBB.play1) END AS enemyName FROM (SELECT * from LAW_BATTLE_RECORD WHERE USER_ID=#{uid} and (type='leitai'))aaa ,
(SELECT * from LAW_BATTLE_PLATFORM WHERE "ID" IN ( SELECT BATTLE_PLATFORM_ID from LAW_BATTLE_RECORD WHERE USER_ID=#{uid} and (type='leitai')
))bbb WHERE AAA.BATTLE_PLATFORM_ID=BBB.id AND (AAA.USER_ID=BBB.play1 OR AAA.USER_ID=BBB.play2)
