mapper.select:
SELECT SUM (STU_COUNT_TIME) FROM LAW_STU_RECORD WHERE user_id = #{userId}
AND STU_FROM = #{stuFrom}
AND STU_TIME >= TO_DATE(#{startTime}, 'yyyy-mm-dd')
AND STU_TIME <= TO_DATE(#{endTime}, 'yyyy-mm-dd')

SELECT RANK () OVER (ORDER BY s.stuTime ASC) AS rankNo FROM ( SELECT U .*, T .stuTime FROM LAW_USER U LEFT JOIN ( SELECT SUM (STU_COUNT_TIME) AS stuTime, USER_ID FROM LAW_STU_RECORD where 1=1
AND STU_FROM = #{stuFrom}
AND STU_TIME >= TO_DATE(#{startTime}, 'yyyy-mm-dd')
AND STU_TIME <= TO_DATE(#{endTime}, 'yyyy-mm-dd')
GROUP BY USER_ID ) T ON U . ID = T .USER_ID ) s WHERE s. ID = #{userId}

SELECT lawAll.ORG_ID as orgId, lawAll.ORG_NAME as orgName, lawAll.org_level as orgLevel, lawAll.parent_id as parentId, lawAll.lawAllCount, videoAll.videoAllCount, picAll.picAllCount, audioAll.audioAllCount , lawTask.lawTaskCount , videoTask.videoTaskCount , picTask.picTaskCount , audioTask.audioTaskCount , videoCase.videoCaseCount , picCase.picCaseCount , audioCase.audioCaseCount
from -- 全部法律法规 (SELECT o.ORG_ID, o.ORG_NAME, o.parent_id, o.org_level, a.lawAllCount FROM LAW_ORG o LEFT JOIN ( SELECT SUM (STU_COUNT) AS lawAllCount, DEPT_ID FROM LAW_STU_RECORD WHERE STU_TYPE = 'law' GROUP BY DEPT_ID
) a on o.ORG_ID = a.DEPT_ID WHERE o.ORG_TYPE IN (10, 70, -1,0) and o.org_status >=2000 Start With o.ORG_ID = #{orgid} Connect By Prior o.ORG_ID = o.parent_id) lawAll,
-- 全部视频数据 (SELECT o.ORG_ID, o.FULL_NAME, o.parent_id, o.org_level, a.videoAllCount FROM LAW_ORG o LEFT JOIN ( SELECT SUM (STU_COUNT) AS videoAllCount, DEPT_ID FROM LAW_STU_RECORD WHERE STU_TYPE = 'stu_video' GROUP BY DEPT_ID
) a on o.ORG_ID = a.DEPT_ID WHERE o.ORG_TYPE IN (10, 70, -1,0) and o.org_status >=2000 Start With o.ORG_ID = #{orgid} Connect By Prior o.ORG_ID = o.parent_id) videoAll,
--全部图文数据 (SELECT o.ORG_ID, o.FULL_NAME, o.parent_id, o.org_level, a.picAllCount FROM LAW_ORG o LEFT JOIN ( SELECT SUM (STU_COUNT) AS picAllCount, DEPT_ID FROM LAW_STU_RECORD WHERE STU_TYPE = 'stu_pic' GROUP BY DEPT_ID
) a on o.ORG_ID = a.DEPT_ID WHERE o.ORG_TYPE IN (10, 70, -1,0) and o.org_status >=2000 Start With o.ORG_ID = #{orgid} Connect By Prior o.ORG_ID = o.parent_id) picAll,
-- 全部音频数据 (SELECT o.ORG_ID, o.FULL_NAME, o.parent_id, o.org_level, a.audioAllCount FROM LAW_ORG o LEFT JOIN ( SELECT SUM (STU_COUNT) AS audioAllCount, DEPT_ID FROM LAW_STU_RECORD WHERE STU_TYPE = 'stu_audio' GROUP BY DEPT_ID
) a on o.ORG_ID = a.DEPT_ID WHERE o.ORG_TYPE IN (10, 70, -1,0) and o.org_status >=2000 Start With o.ORG_ID = #{orgid} Connect By Prior o.ORG_ID = o.parent_id) audioAll,
-- 学习任务法律法规 (SELECT o.ORG_ID, o.FULL_NAME, o.parent_id, o.org_level, a.lawTaskCount FROM LAW_ORG o LEFT JOIN ( SELECT SUM (STU_COUNT) AS lawTaskCount, DEPT_ID FROM LAW_STU_RECORD WHERE STU_TYPE='law' and STU_FROM='learntask' GROUP BY DEPT_ID
) a on o.ORG_ID = a.DEPT_ID WHERE o.ORG_TYPE IN (10, 70, -1,0) and o.org_status >=2000 Start With o.ORG_ID = #{orgid} Connect By Prior o.ORG_ID = o.parent_id) lawTask,
-- 学习任务视频 (SELECT o.ORG_ID, o.FULL_NAME, o.parent_id, o.org_level, a.videoTaskCount FROM LAW_ORG o LEFT JOIN ( SELECT SUM (STU_COUNT) AS videoTaskCount, DEPT_ID FROM LAW_STU_RECORD WHERE STU_TYPE='stu_video' and STU_FROM='learntask' GROUP BY DEPT_ID
) a on o.ORG_ID = a.DEPT_ID WHERE o.ORG_TYPE IN (10, 70, -1,0) and o.org_status >=2000 Start With o.ORG_ID = #{orgid} Connect By Prior o.ORG_ID = o.parent_id) videoTask,
-- 学习任务图文 (SELECT o.ORG_ID, o.FULL_NAME, o.parent_id, o.org_level, a.picTaskCount FROM LAW_ORG o LEFT JOIN ( SELECT SUM (STU_COUNT) AS picTaskCount, DEPT_ID FROM LAW_STU_RECORD WHERE STU_TYPE='stu_pic' and STU_FROM='learntask' GROUP BY DEPT_ID
) a on o.ORG_ID = a.DEPT_ID WHERE o.ORG_TYPE IN (10, 70, -1,0) and o.org_status >=2000 Start With o.ORG_ID = #{orgid} Connect By Prior o.ORG_ID = o.parent_id) picTask,
-- 学习任务音频数据 (SELECT o.ORG_ID, o.FULL_NAME, o.parent_id, o.org_level, a.audioTaskCount FROM LAW_ORG o LEFT JOIN ( SELECT SUM (STU_COUNT) AS audioTaskCount, DEPT_ID FROM LAW_STU_RECORD WHERE STU_TYPE='stu_audio' and STU_FROM='learntask' GROUP BY DEPT_ID
) a on o.ORG_ID = a.DEPT_ID WHERE o.ORG_TYPE IN (10, 70, -1,0) and o.org_status >=2000 Start With o.ORG_ID = #{orgid} Connect By Prior o.ORG_ID = o.parent_id) audioTask,
-- 案例分析视频 (SELECT o.ORG_ID, o.FULL_NAME, o.parent_id, o.org_level, a.videoCaseCount FROM LAW_ORG o LEFT JOIN ( SELECT SUM (STU_COUNT) AS videoCaseCount, DEPT_ID FROM LAW_STU_RECORD WHERE STU_TYPE='stu_video' and STU_FROM='caseana' GROUP BY DEPT_ID
) a on o.ORG_ID = a.DEPT_ID WHERE o.ORG_TYPE IN (10, 70, -1,0) and o.org_status >=2000 Start With o.ORG_ID = #{orgid} Connect By Prior o.ORG_ID = o.parent_id) videoCase,
-- 案例分析图文 (SELECT o.ORG_ID, o.FULL_NAME, o.parent_id, o.org_level, a.picCaseCount FROM LAW_ORG o LEFT JOIN ( SELECT SUM (STU_COUNT) AS picCaseCount, DEPT_ID FROM LAW_STU_RECORD WHERE STU_TYPE='stu_pic' and STU_FROM='caseana' GROUP BY DEPT_ID
) a on o.ORG_ID = a.DEPT_ID WHERE o.ORG_TYPE IN (10, 70, -1,0) and o.org_status >=2000 Start With o.ORG_ID = #{orgid} Connect By Prior o.ORG_ID = o.parent_id) picCase,
-- 案例分析音频数据 (SELECT o.ORG_ID, o.FULL_NAME, o.parent_id, o.org_level, a.audioCaseCount FROM LAW_ORG o LEFT JOIN ( SELECT SUM (STU_COUNT) AS audioCaseCount, DEPT_ID FROM LAW_STU_RECORD WHERE STU_TYPE='stu_audio' and STU_FROM='caseana' GROUP BY DEPT_ID
) a on o.ORG_ID = a.DEPT_ID WHERE o.ORG_TYPE IN (10, 70, -1,0) and o.org_status >=2000 Start With o.ORG_ID = #{orgid} Connect By Prior o.ORG_ID = o.parent_id) audioCase
where lawAll.ORG_ID=videoAll.ORG_ID
and lawAll.ORG_ID=picAll.ORG_ID and lawAll.ORG_ID=audioAll.ORG_ID and lawAll.ORG_ID=lawTask.ORG_ID and lawAll.ORG_ID=videoTask.ORG_ID and lawAll.ORG_ID=picTask.ORG_ID and lawAll.ORG_ID=audioTask.ORG_ID and lawAll.ORG_ID=videoCase.ORG_ID and lawAll.ORG_ID=picCase.ORG_ID and lawAll.ORG_ID=audioCase.ORG_ID
