mapper.select:
SELECT c. ID, c.CASE_TITLE, c.CASE_CODE, c.VIDEO_PIC_ACC, c.CASE_CONTENT, c.CASE_TIME, c.CONTENT_TYPE, r.id as recordId FROM ( SELECT DISTINCT(T.id), T .DATA_ID FROM LAW_TASKS_DESIC T WHERE T .TASK_ID = #{taskId}
AND T .INFO_TYPE = #{infoType}
START WITH T .INFO_ID = #{infoId} CONNECT BY PRIOR T .INFO_ID = T .INFO_PARENT_ID ) M INNER JOIN LAW_CASE_ANALYSIS c ON M .DATA_ID = c. ID LEFT JOIN LAW_STU_RECORD r ON m.DATA_ID=r.stu_id and r.task_id=#{taskId} and r.user_id=#{userId}

SELECT count(1) FROM ( SELECT DISTINCT(T.id), T .DATA_ID FROM LAW_TASKS_DESIC T WHERE T .TASK_ID = #{taskId}
AND T .INFO_TYPE = #{infoType}
START WITH T .INFO_ID = #{infoId} CONNECT BY PRIOR T .INFO_ID = T .INFO_PARENT_ID ) M INNER JOIN LAW_CASE_ANALYSIS c ON M .DATA_ID = c. ID LEFT JOIN LAW_STU_RECORD r ON m.DATA_ID=r.stu_id and r.task_id=#{taskId} and r.user_id=#{userId}

SELECT A .ID AS dataId, 'case_'||A.case_type||'_'||A.ID AS infoId, A .CASE_CODE AS infoCode, A .CASE_TITLE AS infoName, 'case_'||A.case_type||'_'||A.caseLawid AS infoParentId, 'case_'||A.case_type||'_data' AS infoType FROM ( SELECT REGEXP_SUBSTR ( T .CASE_LAWID, '[^,]+', 1, ROWNUM ) AS caseLawid, T .* FROM LAW_CASE_ANALYSIS T CONNECT BY ROWNUM <= LENGTH (T .CASE_LAWID) - LENGTH (REGEXP_REPLACE (T .CASE_LAWID, ',', '')) + 1 ) A WHERE A.del_status = 0
AND A .caseLawid = #{dataId}
AND A .case_type = #{infoType}
AND A.CREATE_user = #{createUser}
and A .case_title like '%'||#{infoName}||'%'

SELECT count(1) FROM ( SELECT REGEXP_SUBSTR ( T .CASE_LAWID, '[^,]+', 1, ROWNUM ) AS caseLawid, T .* FROM LAW_CASE_ANALYSIS T CONNECT BY ROWNUM <= LENGTH (T .CASE_LAWID) - LENGTH (REGEXP_REPLACE (T .CASE_LAWID, ',', '')) + 1 ) A WHERE A.del_status = 0
AND A .caseLawid = #{dataId}
AND A .case_type = #{infoType}
AND A.CREATE_user = #{userId}
and A .case_title like '%'||#{infoName}||'%'
