mapper.select:
SELECT t.id, t.com_content, t.question_difficulty, t.question_type, t.answer_id, t.legal_basis, t.answer_describle, t.answer_choice_number, r.ANSWER AS "userAnswer", r.right FROM law_testquestions t LEFT JOIN LAW_PAPER_RECORD r ON t.id = r.question_id AND r.create_user = #{userId} AND r.task_id = #{id}
t.id IN
#{item}
ORDER BY t.id

mapper.update:
UPDATE LAW_EXERCISE_PAPER
SET ANSWER_NUM = ANSWER_NUM + #{num}

    id = #{id}

mapper.select:
SELECT r.ID AS "recordId", c.NAME AS "themeName", t.ID AS "questId", t.COM_CONTENT AS "content", t.QUESTION_DIFFICULTY AS "difficulty", t.QUESTION_TYPE AS "type", t.ANSWER_ID AS "answer", t.ANSWER_DESCRIBLE AS "describe", r.IS_COLLECT AS "isCollect", r.ANSWER AS "userAnswer" FROM LAW_PAPER_PAPER p LEFT JOIN LAW_PAPER_RECORD r ON p.CONFIGURE_ID = r.TASK_ID AND p.QUEST_ID = r.QUESTION_ID AND r.CREATE_USER = #{userId} LEFT JOIN LAW_TESTQUESTIONS t ON t.ID = p.QUEST_ID LEFT JOIN LAW_SYS_TOPIC c ON c.id = t.SPECIAL_KNOWLEDGE_ID
p.CONFIGURE_ID = #{configureId}
ORDER BY t.QUESTION_TYPE
