mapper.select:
SELECT c.id AS "taskId", t.id AS "id", c.name AS "name", c.theme_name AS "themeName", c.total AS "total", t.status AS "status", t.answer_num AS "answerNum", t.right_num AS "rightNum", c.create_name AS "createName", c.create_time AS "createTime", c.numbers AS "numbers" FROM LAW_EXERCISE_TASK_CONFIGURE c LEFT JOIN LAW_EXERCISE_TASK t ON c.id = t.task_id AND t.create_user = #{createUser}
c.del_flag = #{delFlag}
AND (c.users LIKE '%'||#{users}||'%' OR c.depts LIKE '%'||#{depts}||'%')
AND c.total > 0
ORDER BY ${page.orderBy}
ORDER BY c.create_time DESC

mapper.update:
UPDATE LAW_EXERCISE_TASK
SET ANSWER_NUM = ANSWER_NUM + #{num}

    id = #{id}

UPDATE law_task_record
SET
    IS_COLLECT = #{type}
WHERE ID = #{id}
